import { FaPlus } from "react-icons/fa";

const AddItem = ( {newItem, setNewItem, handleSubmit}) => {
  return (
    <form className='addForm' onSubmit={handle}>
        <input 
        type="text" 
        placeholder="Add Item" 
        autoFocus
        id='addItem'
        required
        value={newItem}
        onChange={(e) => setNewItem(e.target.value)}
        />

        <button 
            type="submit"
            aria-label='Add Item'
        >
            <FaPlus />
        </button>
    </form> 
  )
};

export default AddItem;
